class Solution {
public:
    int minimumBoxes(int n) {
        int sum = 0, cur = 1, ans = 0, prev = 0;
        while (sum + prev + cur < n) sum = sum + prev + cur, prev += cur, ans += cur++;
        return ans + comp(n - sum, cur);
    }
    int comp(int num, int cur) {
        int ans = 1;
        for (int sum = 0, i = 1; sum + i < num; i++) sum += i, ans++;
        return ans;
    }
};
