class Solution {
 public:
     int countBalls(int lowLimit, int highLimit) {
         int ans = 0, list[46] = {0};
         for (int num = lowLimit; num <= highLimit; num++) ans = max(ans, ++list[t(num)]);
         return ans;
     }
     int t(int num) {
         int ans = 0;
         for (; num; num /= 10) ans += num % 10;
         return ans;
     }
 };
