class Solution {
   public:
    int sumOfUnique(vector<int>& nums) {
        int check[110] = {0}, ans = 0;
        for (int i = 0, n = nums.size(); i < n; ++i) {
            ++check[nums[i]];
            if (check[nums[i]] == 2)
                ans -= nums[i];
            else if (check[nums[i]] == 1)
                ans += nums[i];
        }
        return ans;
    }
};
