class Solution {
public:
    bool check(vector<int>& nums) {
        int n = nums.size();
        bool f = false;
        for (int i = 1; i < n; i++) {
            if (nums[i] < nums[i - 1]) {
                if (f) return false;
                f = true;
            }
        }
        return !f || nums[0] >= nums[n - 1];
    }
};
