function maximumScore(a: number, b: number, c: number): number {
  if (a > b) [a, b] = [b, a];
  if (a > c) [a, c] = [c, a];
  if (b > c) [b, c] = [c, b];
  const num1 = Math.min(a, c - b);
  a -= num1;
  c -= num1;
  if (a === 0) return num1 + b;
  else return num1 + (a >> 1) + b;
}
