class Solution {
public:
    int countHomogenous(string s) {
        int n = s.size(), ans = 0, mod = 1e9 + 7;
        for (int i = 0; i < n; i++) {
            long long cnt = 1, start = i;
            while (i + 1 < n && s[i + 1] == s[start]) i++, cnt++;
            ans = (ans + (1 + cnt) * cnt / 2) % mod;
        }
        return ans;
    }
};
