class Solution {
public:
    string mergeAlternately(string word1, string word2) {
        string ans = "";
        for (int i1 = 0, i2 = 0; i1 < word1.size() || i2 < word2.size(); i1++, i2++) {
            if (i1 < word1.size()) ans += word1[i1];
            if (i2 < word2.size()) ans += word2[i2];
        }
        return ans;
    }
};
