class Solution {
public:
    vector<int> minOperations(string boxes) {
        int n = boxes.size(),
            lsum = 0, lcnt = 0,
            rsum = 0, rcnt = 0;
        vector<int> list(n);
        for (int i = 0; i < n; i++) {
            if (boxes[i] == '1') {
                rsum += i;
                rcnt += 1;
            }
        }
        list[0] = rsum;
        for (int i = 1; i < n; i++) {
            if (boxes[i - 1] == '1') rcnt--, lcnt++;
            rsum -= rcnt;
            lsum += lcnt;
            list[i] = lsum + rsum;
        }
        return list;
    }
};
