class Solution {
public:
    bool checkPowersOfThree(int n) {
        if (n % 3 != 0) n--;
        for (; n && n % 3 == 0; n--)
            while (n % 3 == 0) n /= 3;
        return !n;
    }
};

class Solution {
public:
    bool checkPowersOfThree(int n) {
        while (n) {
            if (n % 3 == 2) return false;
            n /= 3;
        }
        return true;
    }
};
