class Solution {
public:
    bool checkOnesSegment(string s) {
        int cnt = 0, n = s.size();
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') continue;
            while (i + 1 < n && s[i + 1] == '1') i++;
            if (++cnt > 1) return false;
        }
        return true;
    }
};
