class Solution {
public:
    int secondHighest(string s) {
        int n1 = -1, n2 = -1;
        for (auto &c : s) {
            if (!isdigit(c)) continue;
            int num = c - '0';
            if (n1 == -1) n1 = num;
            else if (num > n1) n2 = n1, n1 = num;
            else if (num == n1) continue;
            else if (n2 == -1) n2 = num;
            else if (num > n2) n2 = num;
        }
        return n2;
    }
};
