class Solution {
public:
    int maxAscendingSum(vector<int>& nums) {
        int ans = 0;
        for (int i = 0; i < nums.size(); i++) {
            int sum = nums[i];
            while (i + 1 < nums.size() && nums[i + 1] > nums[i]) sum += nums[++i];
            ans = max(ans, sum);
        }
        return ans;
    }
};
