class Solution {
public:
    bool squareIsWhite(string coordinates) {
        return ((coordinates[0] - 'a') & 1) == ((coordinates[1] - '0') & 1);
    }
};

class Solution:
    def squareIsWhite(self, coordinates: str) -> bool:
        return bool((ord(coordinates[0]) - ord('a')) % 2 + int(coordinates[1]) % 2 - 1)
