int findTheWinner(int n, int k) {
    int list[n], current = n - 1;
    for (int i = 0; i < n; i++) list[i] = i + 1;
    list[n - 1] = 0;
    for (int jump = 0; n > 1; n--) {
        jump = (k - 1) % n;
        while (jump > 0) current = list[current], jump--;
        list[current] = list[list[current]];
    }
