func findTheWinner(n int, k int) int {
    list := make([]int, n)
    for i := 0; i < n; i++ {
        list[i] = i + 1
    }
    list[n-1] = 0
    current := n - 1
    for jump := 0; n > 1; n-- {
        jump = (k - 1) % n
        for ; jump > 0; jump-- {
            current = list[current]
        }
        list[current] = list[list[current]]
    }
    return current + 1
}
