class Solution:
    def canSeePersonsCount(self, heights: List[int]) -> List[int]:
        n = len(heights)
        ans = [0] * n
        s = []
        for i in range(n):
            while s and heights[s[-1]] < heights[i]: ans[s.pop()] += 1
            if s: ans[s[-1]] += 1
            s.append(i)
        return ans
