class Solution:
    def minStoneSum(self, piles: List[int], k: int) -> int:
        q = list(-v for v in piles)
        heapify(q)
        res = sum(piles)
        for _ in range(k):
            v = -heappop(q)
            res -= floor(v / 2)
            heappush(q, -(v - floor(v / 2)))
        return res
