class Solution {
   public:
    int countKDifference(vector<int>& nums, int k) {
        int m[300] = {0}, ans = 0;
        for (auto& num : nums) {
            ans += m[num + k + 100] + m[num - k + 100];
            m[num + 100]++;
        }
        return ans;
    }
};

class Solution {
   public:
    int countKDifference(vector<int>& nums, int k) {
        unordered_map<int, int> m;
        int ans = 0;
        for (auto& num : nums) {
            ans += m[num + k] + m[num - k];
            m[num]++;
        }
        return ans;
    }
};
