class Solution {
   public:
    int maximumDifference(vector<int>& nums) {
        int minnum = nums[0], ans = INT_MIN;
        for (auto& num : nums) {
            if (num > minnum) {
                ans = max(ans, num - minnum);
            }
            minnum = min(minnum, num);
        }
        return ans == INT_MIN ? -1 : ans;
    }
};
