class Solution {
   public:
    int maxConsecutiveAnswers(string answerKey, int k) {
        return max(check('T', answerKey, k), check('F', answerKey, k));
    }
    int check(char ch, string str, int k) {
        int n = str.size(), l = 0, r = 0, ans = 0;
        do {
            while (r < n && k > 0) {
                while (r < n && str[r] == ch) r++;
                k--;
                if (r < n) r++;
                while (r < n && str[r] == ch) r++;
            }
            ans = max(ans, r - l);
            while (l < n && k <= 0) {
                if (str[l] != ch) k++;
                l++;
            }
        } while (r < n);
        return ans;
    }
};
