class Solution:
    def timeRequiredToBuy(self, tickets: List[int], k: int) -> int:
        i = 0
        n = len(tickets)
        res = 1
        while True:
            if tickets[i]:
                tickets[i] -= 1
                if i == k and tickets[i] == 0: return res
                res += 1
            i = (i + 1) % n
