class Solution {
        public:
         vector<int> goodDaysToRobBank(vector<int>& security, int time) {
             int n = security.size();
             vector<int> cnts(n, 0);
             for (int i = n - 2; i >= 0; i--) {
                 if (security[i] <= security[i + 1]) {
                     cnts[i] = cnts[i + 1] + 1;
                 } else {
                     cnts[i] = 0;
                 }
             }
             vector<int> ans;
             int prev = 0;
             for (int i = 0; i < n; i++) {
                 if (i > 0 && security[i] <= security[i - 1]) {
                     prev++;
                 } else {
                     prev = 0;
                 }
                 if (prev >= time && cnts[i] >= time) {
                     ans.push_back(i);
                 }
             }
             return ans;
         }
     };
