function rangeBitwiseAnd(m: number, n: number): number {
  let shift = 0;
  while (m < n) {
    m >>= 1;
    n >>= 1;
    ++shift;
  }
  return m << shift;
}

class Solution {
   public:
    int rangeBitwiseAnd(int left, int right) {
        int cnt = 0;
        for (; left != right; cnt++) {
            left >>= 1;
            right >>= 1;
        }
        return left << cnt;
    }
};
