function isIsomorphic(s: string, t: string): boolean {
  const map: Record<string, string> = {};
  const set = new Set<string>();
  for (let i = 0, l = s.length; i < l; i++) {
    const sC = s[i];
    const tC = t[i];
    const mapC = map[sC];
    if (!mapC) {
      if (set.has(tC)) return false;
      map[sC] = tC;
      set.add(tC);
    } else if (mapC !== tC) return false;
  }
  return true;
}
