class Solution {
    public ListNode reverseList(ListNode head) {
        if (head == null || head.next == null)
			return head;
		ListNode newHead=null;
		while(head!=null) {
			ListNode tmp=head.next;
			head.next=newHead;
			newHead=head;
			head=tmp;
		}
		return newHead;
    }
}
