function summaryRanges(nums: number[]): string[] {
  const stack: [number, number][] = [];
  for (const num of nums) {
    if (stack.length === 0) {
      stack.push([num, num]);
    } else {
      const v = stack[stack.length - 1];
      if (num === v[1] + 1) {
        v[1] = num;
      } else {
        stack.push([num, num]);
      }
    }
  }
  return stack.map(([num1, num2]) => (num1 === num2 ? num1 + '' : `${num1}->${num2}`));
}
