function majorityElement(nums: number[]): number[] {
  const map = new Map<number, number>();
  const n = nums.length;
  for (const num of nums) {
    map.set(num, (map.get(num) ?? 0) + 1);
  }
  return Array.from(map.entries())
    .filter(([, v]) => v > n / 3)
    .map(([k]) => k);
}

function majorityElement(nums: number[]): number[] {
  const n = nums.length;
  let num1 = nums[0];
  let num2 = nums[0];
  let val1 = 0;
  let val2 = 0;
  for (const num of nums) {
    if (val1 > 0 && num === num1) {
      val1++;
    } else if (val2 > 0 && num === num2) {
      val2++;
    } else if (val1 === 0) {
      num1 = num;
      val1++;
    } else if (val2 === 0) {
      num2 = num;
      val2++;
    } else {
      val1--;
      val2--;
    }
  }
  let cnt1 = 0;
  let cnt2 = 0;
  for (const num of nums) {
    if (val1 > 0 && num1 === num) cnt1++;
    if (val2 > 0 && num2 === num) cnt2++;
  }
  const ans: number[] = [];
  if (val1 > 0 && cnt1 > n / 3) ans.push(num1);
  if (val2 > 0 && cnt2 > n / 3) ans.push(num2);
  return ans;
}

class Solution {
   public:
    vector<int> majorityElement(vector<int> &nums) {
        int cnt1 = 0, num1, cnt2 = 0, num2;
        for (auto &num : nums) {
            if ((cnt1 == 0 || num1 == num) && num2 != num)
                num1 = num, cnt1++;
            else if (cnt2 == 0 || num2 == num)
                num2 = num, cnt2++;
            else
                cnt1--, cnt2--;
        }
        cnt1 = cnt2 = 0;
        for (auto &num : nums) {
            if (num1 == num)
                cnt1++;
            else if (num2 == num)
                cnt2++;
        }
        vector<int> ans;
        if (cnt1 * 3 > nums.size()) ans.push_back(num1);
        if (cnt2 * 3 > nums.size()) ans.push_back(num2);
        return ans;
    }
};
