class Solution {
    ArrayList<Integer> list = new ArrayList<Integer>();
    public int kthSmallest(TreeNode root, int k) {
    	inorder(root);
    	return list.get(k-1);
    }
	public void inorder(TreeNode node) {
		if (node.left != null)
			inorder(node.left);
		list.add(node.val);
		if (node.right != null)
			inorder(node.right);
	}
}

function kthSmallest(root: TreeNode | null, k: number): number {
  let ans!: number;
  dfs(root);
  return ans;
  function dfs(node: TreeNode | null): void {
    if (node === null) return;
    dfs(node.left);
    if (--k === 0) {
      ans = node.val;
      return;
    }
    dfs(node.right);
  }
}
