function missingNumber(nums: number[]): number {
  const n = nums.length;
  const set = new Set(nums);
  for (let num = 0; num <= n; num++) {
    if (!set.has(num)) return num;
  }
  return 0;
}

function missingNumber(nums: number[]): number {
  const n = nums.length;
  let num = 0;
  for (let i = 0; i <= n; i++) num ^= i;
  for (let i = 0; i < n; i++) num ^= nums[i];
  return num;
}
