function getHint(secret: string, guess: string): string {
  const n = secret.length;
  let a = 0;
  let b = 0;
  const map: Record<string, number> = {};
  const set = new Set<number>();
  for (let i = 0; i < n; i++) {
    const ch1 = secret[i];
    const ch2 = guess[i];
    if (ch1 === ch2) {
      a++;
      set.add(i);
    } else map[ch1] = (map[ch1] ?? 0) + 1;
  }
  for (let i = 0; i < n; i++) {
    if (set.has(i)) continue;
    const ch2 = guess[i];
    if (map[ch2]) {
      b++;
      map[ch2]--;
    }
  }
  return `${a}A${b}B`;
}
