class Solution:
    def earliestFullBloom(self, plantTime: List[int], growTime: List[int]) -> int:
        l = [i for i in range(len(plantTime))]
        l.sort(key=lambda i: growTime[i], reverse=True)
        cur = 0
        res = 0
        for i in l:
            res = max(res, cur + growTime[i] + plantTime[i])
            cur += plantTime[i]
        return res
