class Solution:
    def repeatLimitedString(self, s: str, repeatLimit: int) -> str:
        counter = Counter(s)
        arr = list(counter.items())
        arr.sort()
        ans = ''
        while arr:
            ch, cnt = arr.pop()
            while cnt:
                cur_cnt = min(cnt, repeatLimit)
                cnt -= cur_cnt
                ans += ch * cur_cnt
                if cnt:
                    if arr:
                        ans += arr[-1][0]
                        arr[-1] = (arr[-1][0], arr[-1][1] - 1)
                        if arr[-1][1] == 0:
                            arr.pop()
                    else:
                        break
        return ans
