class Solution:
    def getAncestors(self, n: int, edges: List[List[int]]) -> List[List[int]]:
        nodes=[[] for _ in range(n)]
        for f, t in edges:
            nodes[t].append(f)
        @cache
        def dfs(node):
            ans = []
            for f in nodes[node]:
                ans += [f] + dfs(f)
            return sorted(set(ans))
        return [dfs(i) for i in range(n)]
