class Solution:
    def maximumSubsequenceCount(self, text: str, pattern: str) -> int:
        cnt1 = cnt2 = res = 0
        for c in text:
            if c == pattern[1]:
                res += cnt1
                cnt2 += 1
            if c == pattern[0]:
                cnt1 += 1
        return res + max(cnt1, cnt2)
