class Solution {
public:
    int deleteGreatestValue(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size(), res = 0;
        for (auto &row : grid) sort(row.begin(), row.end());
        for (int j = m - 1; j >= 0; j--) {
            int cur = INT_MIN;
            for (int i = 0; i < n; i++) {
                cur = max(cur, grid[i][j]);
            }
            res += cur;
        }
        return res;
    }
};

class Solution:
    def deleteGreatestValue(self, grid: List[List[int]]) -> int:
        for row in grid:
            row.sort()
        n = len(grid)
        m = len(grid[0])
        res = 0
        for j in range(m):
            num = -inf
            for i in range(n):
                num = max(num, grid[i][j])
            res += num
        return res

impl Solution {
    pub fn delete_greatest_value(mut grid: Vec<Vec<i32>>) -> i32 {
        for row in &mut grid {
            row.sort();
        }
        let mut res = 0;
        for j in 0..grid[0].len() {
            let mut num = i32::MIN;
            for i in 0..grid.len() {
                num = num.max(grid[i][j]);
            }
            res += num;
        }
        res
    }
}
