class Solution:
    def findWinners(self, matches: List[List[int]]) -> List[List[int]]:
        counter = Counter()
        for w, l in matches:
            if not counter[w]: counter[w] = 0
            counter[l] += 1
        res = [[], []] 
        for k, v in sorted(counter.items()):
            if v < 2:
                res[v].append(k)
        return res

class Solution:
            def findWinners(self, matches: List[List[int]]) -> List[List[int]]:
                sets = [set() for _ in range(3)]
                for w, l in matches:
                    if w not in sets[0] and w not in sets[1] and w not in sets[2]:
                        sets[0].add(w)
                    if l not in sets[0] and l not in sets[1] and l not in sets[2]:
                        sets[1].add(l)
                    else:
                        idx = 0
                        while idx < 2:
                            if l in sets[idx]:
                                sets[idx].remove(l)
                                sets[idx + 1].add(l)
                                break
                            idx += 1
                return [sorted(v) for v in sets[:2]]
