class SmallestInfiniteSet:
    def __init__(self):
        self.nmin = 1
        self.q = []
        self.used = set()
    def popSmallest(self) -> int:
        if not self.q:
            self.nmin += 1
            return self.nmin - 1
        num = heappop(self.q)
        self.used.remove(num)
        return num
    def addBack(self, num: int) -> None:
        if self.nmin > num and num not in self.used:
            heappush(self.q, num)
            self.used.add(num)
