class Solution:
    def maximumSum(self, nums: List[int]) -> int:
        m = defaultdict(int)
        ans = -1
        for num in nums:
            v = sum(int(c) for c in str(num))
            if v in m: ans = max(ans, m[v] + num)
            m[v] = max(m[v], num)
        return ans
