class Solution {
public:
    vector<int> applyOperations(vector<int>& nums) {
        int n = nums.size();
        vector<int> list(n, 0);
        int idx = 0;
        for (int i = 0; i < n; i++) {
            if (nums[i] == 0) continue;
            if (i == n - 1) {
                list[idx++] = nums[i];
            } else {
                if (nums[i] == nums[i + 1]) {
                    list[idx++] = nums[i] * 2;
                    nums[i + 1] = 0;
                } else {
                    list[idx++] = nums[i];
                }
            }
        }
        return list;
    }
};

class Solution {
public:
    vector<int> applyOperations(vector<int>& nums) {
        int n = nums.size(), start = 0;
        for (int i = 0; i < n - 1; i++) {
            if (nums[i] == nums[i + 1]) {
                nums[i] *= 2;
                nums[i + 1] = 0;
            }
            if (nums[i] != 0) nums[start++] = nums[i];
        }
        if (nums[n - 1]) nums[start++] = nums[n - 1];
        while (start < n) nums[start++] = 0;
        return nums;
    }
};
