using namespace std;
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    vector<int> list;
    vector<vector<int>> ans;
    vector<vector<int>> closestNodes(TreeNode* root, vector<int>& queries) {
        dfs(root);
        for (auto &q : queries) {
            vector<int> item(2);
            item[0] = find1(q);
            item[1] = find2(q);
            ans.push_back(item);
        }
        return ans;
    }
    void dfs(TreeNode *node) {
        if (!node) return;
        dfs(node->left);
        list.push_back(node->val);
        dfs(node->right);
    }
    int find1(int q) {
        int l = -1, r = list.size() - 1, m;
        while (l < r) {
            m = (l + r + 1) / 2;
            if (list[m] > q) r = m - 1;
            else l = m;
        }
        if (l == -1) return l;
        return list[l];
    }

    int find2(int q) {
        int l = 0, r = list.size(), m;
        while (l < r) {
            m = (l + r) / 2;
            if (list[m] >= q) r = m;
            else l = m + 1;
        }
        if (l == list.size()) return -1;
        return list[l];
    }
};

class Solution:
    def closestNodes(self, root: Optional[TreeNode], queries: List[int]) -> List[List[int]]:
        arr = []
        def inorder(node: Optional[TreeNode]):
            if not node: return
            inorder(node.left)
            arr.append(node.val)
            inorder(node.right)
        inorder(root)
        idx = 0
        ans = [[] for _ in range(len(queries))]
        queries = sorted((q, i) for i, q in enumerate(queries))
        for q, i in queries:
            while idx < len(arr) and arr[idx] < q:
                idx += 1
            ans[i] = [-1, -1]
            if idx < len(arr) and arr[idx] == q:
                ans[i] = [q, q]
            else:
                if idx > 0: ans[i][0] = arr[idx - 1]
                if idx < len(arr): ans[i][1] = arr[idx]
        return ans
