class Solution {
                public:
                    int pivotInteger(int n) {
                        int sum = (1 + n) * n / 2;
                        int prev_sum = 0;
                        for (int i = 1; i <=n ; i++) {
                            int next_sum = sum - prev_sum;
                            prev_sum += i;
                            if (prev_sum == next_sum) return i;
                        }
                        return -1;
                    }
                };

class Solution {
public:
    int pivotInteger(int n) {
        int r = (1 + n) * n / 2, l = 0;
        for (int i = 1; i <= n; i++) {
            l += i;
            if (l == r) return i;
            r -= i;
        }
        return -1;
    }
};

