class Solution:
    def pivotInteger(self, n: int) -> int:
        l = 0
        r = sum(range(1, n + 1))
        for i in range(1, n + 1):
            l += i
            if l == r: return i
            r -= i
        return -1

impl Solution {
    pub fn pivot_integer(n: i32) -> i32 {
        let mut l = 0;
        let mut r: i32 = (1..=n).sum();
        for i in 1..=n {
            l += i;
            if l == r {
                return i
            }
            r -= i;
        }
        -1
    }
}
