class Solution {
public:
    int maximumValue(vector<string>& strs) {
        int res = 0;
        for (auto &s : strs) {
            int cur = 0;
            for (auto &c : s) {
                if (!isdigit(c)) {
                    cur = s.size();
                    break;
                } else {
                    cur = cur * 10 + c - '0';
                }
            }
            res = max(res, cur);
        }
        return res;
    }
};

class Solution:
    def maximumValue(self, strs: List[str]) -> int:
        return max(
            len(s) if not s.isdigit() else int(s)
            for s in strs
        )

impl Solution {
    pub fn maximum_value(strs: Vec<String>) -> i32 {
        strs.into_iter()
            .map(|s| s.parse().unwrap_or(s.len() as i32))
            .max()
            .unwrap()
    }
}
