class Solution {
public:
    int similarPairs(vector<string>& words) {
        unordered_map<int, int> m;
        int ans = 0;
        for (auto &word : words) {
            int num = toNum(word);
            ans += m[num];
            m[num]++;
        }
        return ans;
    }
    int toNum(string &word) {
        int ans = 0;
        for (auto &ch : word) {
            ans |= (1 << (ch - 'a'));
        }
        return ans;
    }
};
