class Solution {
public:
    int countDigits(int num) {
        int res = 0;
        for (int v = num; v; v /= 10) res += num % (v % 10) == 0;
        return res;
    }
};

class Solution:
    def countDigits(self, num: int) -> int:
        return sum(num % int(c) == 0 for c in str(num))

impl Solution {
    pub fn count_digits(num: i32) -> i32 {
        let mut res = 0;
        let mut v = num;
        while v > 0 {
            res += if num % (v % 10) == 0 { 1 } else { 0 };
            v /= 10;
        }
        res
    }
}
