class Solution {
public:
    int differenceOfSum(vector<int>& nums) {
        int num1 = 0, num2 = 0;
        for (int num : nums) num1 += num, num2 += comp(num);
        return abs(num1 - num2);
    }
    int comp(int num) {
        int ans = 0;
        for (; num; num /= 10) ans += num % 10;
        return ans;
    }
};

impl Solution {
    pub fn difference_of_sum(nums: Vec<i32>) -> i32 {
        let mut ans = 0;
        for num in nums {
            ans += num;
            ans -= Solution::comp(num);
        }
        ans.abs()
    }
    fn comp(num: i32) -> i32 {
        let mut num = num;
        let mut ans = 0;
        while num > 0 {
            ans += num % 10;
            num /= 10;
        }
        ans
    }
}
