class Solution:
def alternateDigitSum(self, n: int) -> int:
    ans = 0
    f = 1
    if len(str(n)) % 2 == 0:
        f = -1
    while n:
        ans += f * (n % 10)
        f *= -1
        n //= 10
    return ans

impl Solution {
    pub fn alternate_digit_sum(n: i32) -> i32 {
        let mut n = n;
        let mut ans = 0;
        let mut f = if n.to_string().len() % 2 == 0 { -1 } else { 1 };
        while n > 0 {
            ans += f * (n % 10);
            f *= -1;
            n /= 10;
        }
        return ans;
    }
}
