class Solution {
public:
    int distinctIntegers(int n) {
        queue<int> q;
        unordered_set<int> s;
        q.push(n);
        s.insert(n);
        while (q.size()) {
            int val = q.front();
            q.pop();
            for (int i = 1; i <= min(n, val); i++) {
                if (val % i == 1 && !s.count(i)) {
                    q.push(i);
                    s.insert(i);
                }
            }
        }
        return s.size();
    }
};

from queue import Queue
class Solution:
    def distinctIntegers(self, n: int) -> int:
        q = Queue()
        q.put(n)
        s = set()
        s.add(n)
        while q.qsize():
            val = q.get()
            for i in range(1, min(n, val) + 1):
                if val % i == 1 and i not in s:
                    q.put(i)
                    s.add(i)
        return len(s)

