        let list = s.split('|').collect::<Vec<_>>();
        let mut ans = 0;
        for i in 0..list.len() {
            if i % 2 == 0 {
                for c in list[i].chars() {
                    if c == '*' {
                        ans += 1
                    }
                }
            }
        }
        ans
    }
