class Solution {
public:
    int passThePillow(int n, int time) {
        int cur = 1, d = 1;
        while (time--) {
            cur += d;
            if (cur == n || cur == 1) d *= -1;
        }
        return cur;
    }
};

class Solution:
    def passThePillow(self, n: int, time: int) -> int:
        cur = 1
        d = 1
        while time:
            cur += d
            if cur == n or cur == 1: d *= -1
            time -= 1
        return cur

impl Solution {
    pub fn pass_the_pillow(n: i32, mut time: i32) -> i32 {
        let mut cur = 1;
        let mut d = 1;
        while time > 0 {
            cur += d;
            if cur == n || cur == 1 {
                d *= -1;
            }
            time -= 1;
        }
        cur
    }
}
